import os
from Crypto.Util.number import bytes_to_long, getPrime

flag = os.getenvb(b"FLAG", b"he2024{fake}")
assert flag.startswith(b"he2024{")

m = bytes_to_long(flag)
e = 0x10001
p = getPrime(1024)
q = getPrime(1024)
n = p * q
e = 65537
phi = (p - 1) * (q - 1)
d = pow(e, -1, phi)
c = pow(m, e, n)

print(f"e={e}")
print(f"d={d}")
print(f"hint_1={n - p}")
print(f"hint_2={n - q}")
print(f"c={c}")

